
ADMIN_TEAM_NAME = 'admin'
BACKOFFICE_TEAM_NAME = 'backoffice'


def get_team(identity):
    return identity['claims']['custom:team']


def is_admin(identity):
    return get_team(identity) == ADMIN_TEAM_NAME


def is_backoffice(identity):
    return get_team(identity) == BACKOFFICE_TEAM_NAME


def is_admin_or_backoffice(identity):
    return is_admin(identity) or is_backoffice(identity)


def get_username(identity):
    return identity['username']


def get_email(identity):
    return identity['claims']['email']
