from .base import BaseApi, rex_result


class UserGroupsApi(BaseApi):

    @rex_result()
    def get(self, group_id, raw=False):
        data = {'id': group_id}
        return self.client.request('AdminUserGroups::read', data)

    @rex_result()
    def search(self, name=None, raw=False):
        data = {'criteria': [], 'limit': 20}
        if name:
            data['criteria'].append(
                {'name': 'name', 'type': '=', 'value': name})

        return self.client.request('AdminUserGroups::search', data)
